"use strict";(self.webpackChunkbloksec_docs=self.webpackChunkbloksec_docs||[]).push([[4665],{7972:e=>{e.exports=JSON.parse('{"label":"SAML2","permalink":"/docs/tags/saml-2","allTagsPath":"/docs/tags","count":5,"items":[{"id":"integrations/integration-cyberark","title":"CyberArk","description":"BlokSec provides decentralized passwordless authentication to CyberArk Privileged Access Manager solution using either OpenID Connect (OIDC) or Security Assertion Markup Language (SAML) 2.0 authentication standard. BlokSec acts as an identity provider (IdP), authenticating users using strong cryptographic based digital signature that is immutable and tamper-proof.","permalink":"/docs/integrations/integration-cyberark"},{"id":"integrations/integration-dropbox","title":"Dropbox","description":"BlokSec can be configured to support passwordless login to Dropbox.","permalink":"/docs/integrations/integration-dropbox"},{"id":"integrations/integration-google-workspace","title":"Google Workspace","description":"BlokSec can be configured to support passwordless login to your Google Workspace, and also supports provisioning.","permalink":"/docs/integrations/integration-google-workspace"},{"id":"integrations/integration-ibmsecurityverify","title":"IBM Security Verify","description":"BlokSec can be configured as an inbound federation identity provider (a.k.a. social provider) for your IBM Security Verify tenant, and can also be configured to enable mapping of users with in the IBM Admin console.  The following article describes the process to configure authentication .","permalink":"/docs/integrations/integration-ibmsecurityverify"},{"id":"integrations/integration-slack","title":"Slack","description":"BlokSec can be configured to support passwordless login to Slack via SAML2.","permalink":"/docs/integrations/integration-slack"}],"unlisted":false}')}}]);